import React from "react";
import StudentHeader from "./StudentHeader";
import FloatingChatButton from "./FloatingChatButton";
import bg from "../assets/bg.png";
import bg2 from "../assets/bg2.png";

const StudentLayout = ({ children }) => {
  const User = JSON.parse(localStorage.getItem("User"));
  const studentId = User?.student?._id;

  return (
    <div className="min-h-screen relative bg-gray-50 dark:bg-gray-900">
      {/* Main background with image */}
      <div
        className="fixed inset-0 opacity-10 dark:opacity-5"
        style={{
          backgroundImage: `url(${bg2})`,
          backgroundSize: "cover",
          backgroundPosition: "center",
          backgroundRepeat: "no-repeat",
        }}
      />

      {/* Gradient overlays */}
      <div className="fixed inset-0">
        <div className="absolute inset-0 bg-gradient-to-br from-blue-400/30 to-purple-500/30 dark:from-blue-900/40 dark:to-purple-900/40 mix-blend-overlay" />
        <div className="absolute inset-0 bg-gradient-to-tr from-yellow-200/20 via-transparent to-pink-300/20 dark:from-yellow-900/20 dark:to-pink-900/20 mix-blend-multiply" />
      </div>

      {/* Animated grain effect */}
      <div className="fixed inset-0 opacity-[0.15] dark:opacity-[0.05]">
        <div
          className="absolute inset-0 bg-repeat animate-grain"
          style={{
            backgroundImage:
              'url("data:image/png;base64,")',
            backgroundSize: "100px 100px",
          }}
        />
      </div>

      {/* Content */}
      <div className="relative z-10">
        <StudentHeader studentId={studentId} />
        <main className="container mx-auto px-4 py-8">
          <div className="bg-white/80 dark:bg-gray-800/80 backdrop-blur-md rounded-2xl shadow-xl p-6 transition-all duration-300 hover:shadow-2xl">
            {children}
          </div>
        </main>
        <FloatingChatButton />
      </div>
    </div>
  );
};

export default StudentLayout;
